CREATE PROCEDURE MyProc\n    @TableName sysname,\n    @Param1 int,\n    @Param2 varchar(50)\nAS\nBEGIN\n    -- Whitelist of allowed table names\n    IF @TableName NOT IN ('Table1', 'Table2', 'Table3')\n    BEGIN\n        RAISERROR ('Invalid table name', 16, 1);\n        RETURN;\n    END\n\n    -- Build dynamic SQL query\n    DECLARE @sql nvarchar(max) = N'SELECT * FROM ' + QUOTENAME(@TableName) + N' WHERE Column1 = @Param1 AND Column2 = @Param2';\n\n    -- Execute dynamic SQL query with parameters\n    EXEC sp_executesql @sql, N'@Param1 int, @Param2 varchar(50)', @Param1 = @Param1, @Param2 = @Param2;\nEND;