CREATE TABLE #LargeTable (id INT, name VARCHAR(100));\nINSERT INTO #LargeTable (id, name) VALUES (1, 'John'), (2, 'Jane'), ..., (1000000, 'Bob');\n\nCREATE TABLE #PartitionedTable (id INT, name VARCHAR(100));\nINSERT INTO #PartitionedTable (id, name) VALUES (1, 'John'), (2, 'Jane'), ..., (1000000, 'Bob');\n\nCREATE PARTITION SCHEME ps ON id RANGE LEFT FOR VALUES (100000, 200000, 300000, 400000, 500000);\nCREATE PARTITION FUNCTION pf (id INT) AS RANGE LEFT FOR VALUES (100000, 200000, 300000, 400000, 500000);\nCREATE TABLE #PartitionedTable (id INT, name VARCHAR(100)) ON ps (id);\n\n-- Using INNER JOIN\nSELECT * \nFROM #LargeTable lt\nINNER JOIN #PartitionedTable pt ON lt.id = pt.id\nWHERE lt.id BETWEEN 200000 AND 300000;\n\n-- Using CROSS APPLY\nSELECT * \nFROM #LargeTable lt\nCROSS APPLY (\n    SELECT * \n    FROM #PartitionedTable pt\n    WHERE pt.id = lt.id AND pt.id BETWEEN 200000 AND 300000\n) AS ca;