-- Create a hash table for each dimension table\nCREATE TEMP TABLE host_hash AS\nSELECT id, name, hash(name) AS hash_value\nFROM host;\n\nCREATE TEMP TABLE etype_hash AS\nSELECT id, name, hash(name) AS hash_value\nFROM etype;\n\n-- ... create hash tables for other dimension tables ...\n\n-- Join the fact table with the hash tables\nINSERT INTO event (time, status, fk_host, fk_etype, ... )\nSELECT t.time, t.status, host_hash.id, etype_hash.id ...\nFROM temp_table AS t\nJOIN host_hash ON t.host_name = host_hash.name AND t.host_name = host_hash.hash_value\nJOIN etype_hash ON t.etype = etype_hash.name AND t.etype = etype_hash.hash_value\n... join with other hash tables ...