-- Create a spatial index on a table\nCREATE TABLE MyTable (\n    id INT PRIMARY KEY,\n    location GEOGRAPHY\n);\n\nCREATE SPATIAL INDEX MySpatialIndex ON MyTable (location);\n\n-- Use full-text search to query a table\nCREATE TABLE MyTable (\n    id INT PRIMARY KEY,\n    text VARCHAR(100)\n);\n\nCREATE FULLTEXT INDEX ON MyTable (text);\n\nSELECT * FROM MyTable WHERE CONTAINS(text, 'search_term');