CREATE PROCEDURE DeleteCountryAndRelatedRecords\n    @Country_ID INT\nAS\nBEGIN\n    BEGIN TRANSACTION;\n\n    DELETE FROM Areas\n    WHERE RegionID IN (\n        SELECT Region_ID\n        FROM Regions\n        WHERE Country_ID = @Country_ID\n    );\n\n    DELETE FROM Regions\n    WHERE Country_ID = @Country_ID;\n\n    DELETE FROM Country\n    WHERE Country_ID = @Country_ID;\n\n    COMMIT TRANSACTION;\nEND;