CREATE TABLE temp.ParentTable (\n    ParentID INT IDENTITY(1,1) PRIMARY KEY,\n    -- other columns...\n);\n\nCREATE TABLE temp.ChildTable (\n    ChildID INT IDENTITY(1,1) PRIMARY KEY,\n    ParentID INT,\n    -- other columns...\n    CONSTRAINT FK_ChildTable_ParentTable FOREIGN KEY (ParentID) REFERENCES temp.ParentTable(ParentID)\n);\n\nCREATE TABLE temp.GrandChildTable (\n    GrandChildID INT IDENTITY(1,1) PRIMARY KEY,\n    ChildID INT,\n    -- other columns...\n    CONSTRAINT FK_GrandChildTable_ChildTable FOREIGN KEY (ChildID) REFERENCES temp.ChildTable(ChildID)\n);