SELECT \n    datepart(year,[Registration]) AS YearRegistered,\n    Model, \n    Make,\n    (SELECT MIN(SalePrice) \n     FROM [VehicleSales] v2 \n     WHERE v2.Make = v1.Make \n     AND v2.Model = v1.Model \n     AND datepart(year,v2.[Registration]) = datepart(year,v1.[Registration]))\n     AS StartingPrice\n\nFROM [VehicleSales] v1\nGROUP BY \n    datepart(year,[Registration]),\n    Model, \n    Make