CREATE TABLE samples (\n  sample_id INT PRIMARY KEY,\n  sample_template_id INT,\n  FOREIGN KEY (sample_template_id) REFERENCES sample_templates(sample_template_id)\n);\n\nCREATE TABLE sample_templates (\n  sample_template_id INT PRIMARY KEY,\n  version INT,\n  status VARCHAR(50),\n  date_created DATE,\n  name VARCHAR(100)\n);\n\nCREATE TABLE sample_attributes (\n  sample_attribute_id INT PRIMARY KEY,\n  name VARCHAR(100),\n  description VARCHAR(255)\n);\n\nCREATE TABLE sample_values (\n  sample_id INT,\n  sample_attribute_id INT,\n  value_type VARCHAR(20),  -- e.g., 'number', 'date', 'string'\n  value VARCHAR(255),\n  PRIMARY KEY (sample_id, sample_attribute_id),\n  FOREIGN KEY (sample_id) REFERENCES samples(sample_id),\n  FOREIGN KEY (sample_attribute_id) REFERENCES sample_attributes(sample_attribute_id)\n);