SET @OrderByColumn = 'AddedDate';\nSET @OrderDirection = 1;  -- 1 for ASC, 2 for DESC\n\nSELECT\n    -- Your columns here\nFROM\n    -- Your table here\nORDER BY\n    CASE WHEN @OrderDirection = 1 THEN\n        CASE\n            WHEN @OrderByColumn = 'AddedDate' THEN AddedDate\n            WHEN @OrderByColumn = 'Visible' THEN Visible\n            WHEN @OrderByColumn = 'AddedBy' THEN AddedBy\n            WHEN @OrderByColumn = 'Title' THEN Title\n        END\n    END ASC,\n    CASE WHEN @OrderDirection = 2 THEN\n        CASE\n            WHEN @OrderByColumn = 'AddedDate' THEN AddedDate\n            WHEN @OrderByColumn = 'Visible' THEN Visible\n            WHEN @OrderByColumn = 'AddedBy' THEN AddedBy\n            WHEN @OrderByColumn = 'Title' THEN Title\n        END\n    END DESC;