declare @next as varchar(255)\ndeclare @owner varchar(255)\ndeclare @t as nvarchar(255)\ndeclare @sql as nvarchar(255)\n\nwhile 1=1\nbegin\n  set @next = (select top 1 o.name \n               FROM syscolumns c \n               inner join sysobjects o on c.id = o.id \n               where c.name = 'criteria1' and o.id > @next \n               order by o.id)\n\n  if @next is null\n    break\n  else\n  begin\n    set @owner = (select top 1 u.name \n                  FROM syscolumns c \n                  inner join sysobjects o on c.id = o.id \n                  left join sysusers u on o.uid=u.uid\n                  where c.name = 'criteria1' and o.id = @next \n                  order by o.id)\n\n    set @t = @owner+'.'+@next\n    set @sql = N'select criteria1 from ' + @t + ' where linenum = 1'\n    exec sp_executesql @sql\n  end\n  continue\nend