SELECT\n    -- Extract the last set of parentheses for info 1\n    (REGEXP_MATCHES(ColumnX, '\(([^)]+)\)', 'g'))[ARRAY_LENGTH(REGEXP_MATCHES(ColumnX, '\(', 'g'), 1)] AS Column1,\n\n    -- Extract the first set of parentheses for info 2, if exists\n    CASE\n        WHEN ARRAY_LENGTH(REGEXP_MATCHES(ColumnX, '\(', 'g'), 1) > 1 THEN\n            (REGEXP_MATCHES(ColumnX, '\(([^)]+)\)', 'g'))[1]\n        ELSE\n            NULL\n    END AS Column2\nFROM YourTable;