DECLARE @a INT \nDECLARE @b VARCHAR(10) -- specify the length of the VARCHAR variable\nDECLARE @sql nvarchar(max) -- declare a variable to hold the dynamic SQL\n\nSET @a = 1\n\nWHILE @a < 30\nBEGIN\n    SET @b = CONVERT(VARCHAR(10), @a) -- convert the INT to VARCHAR\n    \n    SET @sql = N'UPDATE source SET h = h + x_' + @b + N' WHERE y_' + @b + N' = ''Sold''' -- build the dynamic SQL\n    \n    EXEC sp_executesql @sql -- execute the dynamic SQL\n    \n    SET @a = @a + 1\nEND