CREATE TABLE Class (\n  ClassID INT PRIMARY KEY,\n  Type VARCHAR(10) NOT NULL CHECK(Type IN ('Coded', 'Numeric', 'String'))\n);\n\nCREATE TABLE ClassCoded (\n  ClassID INT PRIMARY KEY,\n  Label VARCHAR(50),\n  Value INT,\n  FOREIGN KEY (ClassID) REFERENCES Class(ClassID),\n  CHECK(Class.Type = 'Coded')\n);\n\nCREATE TABLE ClassNumeric (\n  ClassID INT PRIMARY KEY,\n  MinValue DECIMAL(10, 2),\n  MaxValue DECIMAL(10, 2),\n  FOREIGN KEY (ClassID) REFERENCES Class(ClassID),\n  CHECK(Class.Type = 'Numeric')\n);\n\nCREATE TABLE ClassString (\n  ClassID INT PRIMARY KEY,\n  Pattern VARCHAR(100),\n  FOREIGN KEY (ClassID) REFERENCES Class(ClassID),\n  CHECK(Class.Type = 'String')\n);