CREATE FUNCTION dbo.ReplaceLt(@str nvarchar(max))\nRETURNS nvarchar(max)\nAS\nBEGIN\n    DECLARE @result nvarchar(max)\n    SET @result = ''\n    DECLARE @i int\n    SET @i = 1\n    WHILE @i <= LEN(@str)\n    BEGIN\n        IF SUBSTRING(@str, @i, 3) = '&lt'\n        BEGIN\n            SET @result = @result + '&lt;'\n            SET @i = @i + 3\n        END\n        ELSE\n        BEGIN\n            SET @result = @result + SUBSTRING(@str, @i, 1)\n            SET @i = @i + 1\n        END\n    END\n    RETURN @result\nEND