SELECT c.*\nFROM Customers c\nJOIN (\n  SELECT o.CustomerID, SUM(p.Price * o.Quantity) AS Volume1995\n  FROM Orders o\n  JOIN Products p ON o.ProductID = p.ID\n  WHERE YEAR(o.Date) = 1995\n  GROUP BY o.CustomerID\n) AS v1995 ON c.ID = v1995.CustomerID\nJOIN (\n  SELECT o.CustomerID, SUM(p.Price * o.Quantity) AS Volume1996\n  FROM Orders o\n  JOIN Products p ON o.ProductID = p.ID\n  WHERE YEAR(o.Date) = 1996\n  GROUP BY o.CustomerID\n) AS v1996 ON c.ID = v1996.CustomerID\nWHERE v1996.Volume1996 > v1995.Volume1995;