WITH prioritized_rows AS (\n  SELECT *,\n    ROW_NUMBER() OVER (\n      PARTITION BY longitude, latitude, businessName\n      ORDER BY \n        CASE \n          WHEN url IS NOT NULL THEN 1\n          WHEN caption IS NOT NULL THEN 2\n          ELSE 3\n        END\n    ) AS row_priority\n  FROM your_table\n)\nDELETE FROM your_table\nWHERE rowID IN (\n  SELECT rowID\n  FROM prioritized_rows\n  WHERE row_priority > 1\n);