CREATE TABLE prospect (\n  prospect_id INT PRIMARY KEY,\n  -- other columns...\n);\n\nCREATE TABLE external_status (\n  external_status_id INT PRIMARY KEY,\n  prospect_id INT NOT NULL,\n  system_id INT NOT NULL,  -- 1 or 2, depending on the external system\n  status_id INT NOT NULL,\n  FOREIGN KEY (prospect_id) REFERENCES prospect(prospect_id)\n);\n\nCREATE TABLE status_values (\n  status_id INT PRIMARY KEY,\n  value VARCHAR(50) NOT NULL\n);