SELECT e.*\nFROM Employees e\nJOIN (\n  SELECT o.EmployeeID, SUM(p.Price * o.Quantity) AS Volume\n  FROM Orders o\n  JOIN Products p ON o.ProductID = p.ID\n  GROUP BY o.EmployeeID\n) AS v ON e.ID = v.EmployeeID\nWHERE v.Volume < (SELECT AVG(Volume) FROM (\n  SELECT o.EmployeeID, SUM(p.Price * o.Quantity) AS Volume\n  FROM Orders o\n  JOIN Products p ON o.ProductID = p.ID\n  GROUP BY o.EmployeeID\n) AS avg_v);