VAR result_set REFCURSOR\n\nDECLARE\n   my_tbl_1 NUMBER := 1;\n   my_tbl_2 NUMBER := 0;\n   my_tbl_3 NUMBER := 0;\nBEGIN\n   IF (my_tbl_1 > 0)\n   THEN\n      OPEN :result_set FOR\n         SELECT   *\n             FROM my_tbl_1\n         ORDER BY my_tbl_1_id DESC;\n   ELSIF (my_tbl_2 > 0)\n   THEN\n      OPEN :result_set FOR\n         SELECT   *\n             FROM my_tbl_2\n         ORDER BY my_tbl_2_id DESC;\n   ELSIF (my_tbl_3 > 0)\n   THEN\n      OPEN :result_set FOR\n         SELECT   *\n             FROM my_tbl_3\n         ORDER BY my_tbl_3_id DESC;\n   END IF;\nEND;\n\nPRINT result_set