(\n  SELECT \n    m.*,\n    u.username,\n    u.email\n  FROM \n    microblog m\n  JOIN \n    users u ON m.user_id = u.id\n  WHERE \n    m.user_id = ?\n)\nUNION ALL\n(\n  SELECT \n    m.*,\n    u.username,\n    u.email\n  FROM \n    microblog m\n  JOIN \n    users u ON m.user_id = u.id\n  JOIN \n    relations r ON u.id = r.following_id\n  WHERE \n    r.follower_id = ?\n)\nORDER BY \n  posted DESC;