-- Create a pass-through query to SQL Server\nCREATE PROCEDURE GetSQLServerData\nAS\nBEGIN\n    SELECT * FROM YourSQLServerTable\nEND;\n\n-- Create a pass-through query to Oracle\nCREATE PROCEDURE GetOracleData\nAS\nBEGIN\n    SELECT * FROM YourOracleTable\nEND;\n\n-- Create a local query to join the results\nSELECT *\nFROM (EXEC GetSQLServerData) AS sqlData\nINNER JOIN (EXEC GetOracleData) AS oracleData\nON sqlData.JoinColumn = oracleData.JoinColumn;