SELECT number, SUM(frequency) as total_frequency\nFROM (\n  SELECT n1 as number, COUNT(n1) as frequency FROM lottery GROUP BY n1\n  UNION ALL\n  SELECT n2 as number, COUNT(n2) as frequency FROM lottery GROUP BY n2\n  UNION ALL\n  SELECT n3 as number, COUNT(n3) as frequency FROM lottery GROUP BY n3\n  UNION ALL\n  SELECT n4 as number, COUNT(n4) as frequency FROM lottery GROUP BY n4\n  UNION ALL\n  SELECT n5 as number, COUNT(n5) as frequency FROM lottery GROUP BY n5\n  UNION ALL\n  SELECT n6 as number, COUNT(n6) as frequency FROM lottery GROUP BY n6\n) AS subquery\nGROUP BY number;