-- Create new files on the 10000 RPM drive\nALTER DATABASE [logging_database] ADD FILE (\n    NAME = N'new_data_file',\n    FILENAME = N'path_to_new_data_file.mdf',\n    SIZE = 100MB,\n    FILEGROWTH = 10MB\n);\nALTER DATABASE [logging_database] ADD LOG FILE (\n    NAME = N'new_log_file',\n    FILENAME = N'path_to_new_log_file.ldf',\n    SIZE = 100MB,\n    FILEGROWTH = 10MB\n);\n\n-- Migrate data to new files\nDBCC SHRINKFILE ('old_data_file', EMPTYFILE);\nDBCC SHRINKFILE ('old_log_file', EMPTYFILE);