CREATE TABLE GTW_WORKFLOW_MON \n(\n    WORKFLOW_NAME LVARCHAR(2048) NOT NULL, \n    WORKFLOW_LOADED NUMERIC(20) NOT NULL, \n    ACTIVITY_NAME LVARCHAR(2048) NOT NULL,\n    FLAGS INTEGER NOT NULL,\n    MONITOR_NAME LVARCHAR(2048) NOT NULL,\n    CLASSNAME LVARCHAR(2048) NOT NULL,\n    STR0 LVARCHAR(2048),\n    STR1 LVARCHAR(2048),\n    STR2 LVARCHAR(2048), \n    NUM0 LVARCHAR(2048), \n    NUM1 LVARCHAR(2048), \n    NUM2 LVARCHAR(2048), \n    DATE0 LVARCHAR(2048),\n    DATE1 LVARCHAR(2048), \n    DATE2 LVARCHAR(2048), \n    PRIMARY KEY (WORKFLOW_NAME, WORKFLOW_LOADED, ACTIVITY_NAME, MONITOR_NAME)\n)