-- Insert the new permission into the Permissions table\nINSERT INTO Permissions (name) VALUES ('canSubmitForms');\n\n-- Assign the new permission to the User level in the UserLevelPermissions table\nINSERT INTO UserLevelPermissions (user_level_id, permission_id) \nVALUES ((SELECT id FROM UserLevels WHERE name = 'User'), \n        (SELECT id FROM Permissions WHERE name = 'canSubmitForms'));\n\n-- Update the UserFacilityPermissions table to reflect the new permission\nUPDATE UserFacilityPermissions \nSET user_level_id = (SELECT id FROM UserLevels WHERE name = 'User')\nWHERE facility_id = (SELECT id FROM Facilities WHERE name = 'ABCInc')\n  AND user_id = (SELECT id FROM Users WHERE username = 'joebob');