CREATE OR REPLACE FUNCTION send_data_over_tcp_on_insert()\nRETURNS trigger AS $$\nDECLARE\n    conn pg8000_socket;\nBEGIN\n    conn = pg8000_connect('localhost', 8080);\n    IF conn IS NOT NULL THEN\n        pg8000_send(conn, 'Row inserted!');\n        pg8000_close(conn);\n    END IF;\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER send_data_over_tcp_on_insert_trigger\nAFTER INSERT ON mytable\nFOR EACH ROW\nEXECUTE PROCEDURE send_data_over_tcp_on_insert();