CREATE OR REPLACE PROCEDURE update_sequences AS\nBEGIN\n  FOR rec IN (SELECT sequence_name, table_name, column_name \n              FROM user_sequences \n              JOIN user_tables ON user_sequences.sequence_name = user_tables.table_name || '_SEQ'\n              JOIN user_tab_columns ON user_tables.table_name = user_tab_columns.table_name \n                                    AND user_tab_columns.column_id = 1) LOOP\n    EXECUTE IMMEDIATE 'ALTER SEQUENCE ' || rec.sequence_name || ' RESTART WITH ' || \n                      (SELECT MAX(rec.column_name) + 1 FROM rec.table_name);\n  END LOOP;\nEND;\n/