-- Create a table with some data\nCREATE TABLE tablex (id INT, name VARCHAR(10));\nINSERT INTO tablex (id, name) VALUES (11, 'A'), (21, 'B'), (31, 'C'), (41, 'D'), (51, 'E'), (61, 'F');\n\n-- Set the isolation level to REPEATABLE READ\nSET TRANSACTION ISOLATION LEVEL REPEATABLE READ;\n\n-- Start a transaction\nBEGIN;\n\n-- First fetch (A)\nSELECT * FROM tablex LIMIT 2;\n\n-- Delete a record (B)\nDELETE FROM tablex WHERE id = 21;\n\n-- Second fetch (A)\nSELECT * FROM tablex LIMIT 2 OFFSET 2;\n\n-- Commit the transaction\nCOMMIT;