CREATE AGGREGATE GroupConcat (@value nvarchar(max))\nRETURNS nvarchar(max)\nEXTERNAL NAME GroupConcat;\n\nCREATE FUNCTION GroupConcat_Init()\nRETURNS @result nvarchar(max)\nAS\nBEGIN\n    SET @result = '';\n    RETURN @result;\nEND;\n\nCREATE FUNCTION GroupConcat_Accumulate(@result nvarchar(max), @value nvarchar(max))\nRETURNS nvarchar(max)\nAS\nBEGIN\n    SET @result = @result + @value + ' / ';\n    RETURN @result;\nEND;\n\nCREATE FUNCTION GroupConcat_Merge(@result1 nvarchar(max), @result2 nvarchar(max))\nRETURNS nvarchar(max)\nAS\nBEGIN\n    SET @result1 = @result1 + @result2;\n    RETURN @result1;\nEND;\n\nCREATE FUNCTION GroupConcat_Terminate(@result nvarchar(max))\nRETURNS nvarchar(max)\nAS\nBEGIN\n    SET @result = LEFT(@result, LEN(@result) - 3); -- remove trailing ' / '\n    RETURN @result;\nEND;