-- Create a stored procedure\nCREATE PROCEDURE MyProcedure\n    @TableName varchar(100),\n    @FieldName varchar(100)\nAS\nBEGIN\n    DECLARE @SQL varchar(250)\n    SET @SQL = 'SELECT ' + @FieldName + ' FROM ' + @TableName\n    EXEC sp_executesql @SQL\nEND\nGO\n\n-- Create a table variable\nDECLARE @Data TABLE (FieldValue varchar(100))\n\n-- Insert data into the table variable using the stored procedure\nINSERT INTO @Data\nEXEC MyProcedure 'Person.Address', 'AddressID'\n\n-- Use the table variable as needed\nSELECT * FROM @Data