-- Create a temporary table to store intermediate results\nCREATE TABLE #tmpResults (\n    id INT,\n    name VARCHAR(50)\n);\n\n-- Insert data into the temporary table\nINSERT INTO #tmpResults (id, name)\nSELECT id, name\nFROM large_table\nWHERE filter_condition = 1;\n\n-- Use the temporary table to join with other tables\nSELECT *\nFROM #tmpResults\nJOIN other_table ON #tmpResults.id = other_table.id;\n\n-- Drop the temporary table\nDROP TABLE #tmpResults;