CREATE TRIGGER trg_encrypt_creditcard ON your_table\nFOR INSERT\nAS\nBEGIN\n    DECLARE @encryptedCreditCardNumber varbinary(max)\n    DECLARE @CreditCardNumber varchar(max)\n\n    SET @CreditCardNumber = (SELECT creditcard FROM inserted)\n    SET @encryptedCreditCardNumber = EncryptByKey(Key_GUID('testKey'), @CreditCardNumber)\n\n    UPDATE your_table\n    SET creditcard = CONVERT(varchar(max), @encryptedCreditCardNumber, 2)\n    WHERE creditcard = @CreditCardNumber\nEND