SELECT \n  s.samplepointid,\n  o.oildatasetid,\n  os.oildatasetstatusid,\n  CASE \n    WHEN os.oildatasetstatusid = 5 THEN 16\n    WHEN os.oildatasetstatusid = 6 THEN 17\n    WHEN os.oildatasetstatusid IN (7, 8, 9) THEN 18\n    WHEN os.oildatasetstatusid = 10 THEN 19\n    WHEN os.oildatasetstatusid = 11 THEN 20\n  END AS new_oildatasetstatusid\nFROM \n  samplepoint s\n  INNER JOIN oildataset o ON s.samplepointid = o.samplepointid\n  INNER JOIN Oildatasetstatus os ON o.oildatasetid = os.oildatasetid\nWHERE \n  o.oillabid = 4;