CREATE FUNCTION dbo.StripHTML (@html varchar(max))\nRETURNS varchar(max)\nAS\nBEGIN\n    DECLARE @start int, @end int, @len int\n    SET @start = PATINDEX('%<%>%', @html)\n    WHILE @start > 0\n    BEGIN\n        SET @end = PATINDEX('%>%', @html, @start)\n        SET @len = @end - @start + 1\n        SET @html = STUFF(@html, @start, @len, '')\n        SET @start = PATINDEX('%<%>%', @html)\n    END\n    SET @html = REPLACE(@html, '&lt;', '<')\n    SET @html = REPLACE(@html, '&gt;', '>')\n    RETURN @html\nEND