-- Create a sample table with a clustered index on a non-unique column\nCREATE TABLE Comments (\n    CommentId INT PRIMARY KEY,\n    AnswerId INT,\n    CommentText VARCHAR(255)\n);\n\nCREATE CLUSTERED INDEX IX_Comments_AnswerId ON Comments (AnswerId);\n\n-- Insert 1,000,000 rows with random AnswerId values\nDECLARE @i INT = 0;\nWHILE @i < 1000000\nBEGIN\n    INSERT INTO Comments (AnswerId, CommentText)\n    VALUES (ABS(CHECKSUM(NEWID())) % 100000, 'Sample comment text');\n    SET @i += 1;\nEND;\n\n-- Measure insert performance\nSET STATISTICS TIME ON;\nINSERT INTO Comments (AnswerId, CommentText)\nVALUES (ABS(CHECKSUM(NEWID())) % 100000, 'Sample comment text');\nSET STATISTICS TIME OFF;