WITH RecursiveChunks AS (\n    SELECT \n        MyVarbinaryField,\n        1 AS ChunkNumber,\n        SUBSTRING(MyVarbinaryField, 1, 1024) AS Chunk\n    FROM MyTable\n\n    UNION ALL\n\n    SELECT \n        MyVarbinaryField,\n        ChunkNumber + 1,\n        SUBSTRING(MyVarbinaryField, (ChunkNumber * 1024) + 1, 1024)\n    FROM RecursiveChunks\n    WHERE DATALENGTH(MyVarbinaryField) > (ChunkNumber * 1024)\n)\nSELECT * FROM RecursiveChunks;