#!/bin/bash\n\nclassify_dirs() {\n    for dir in "$@"; do\n        if [ -d "$dir" ]; then\n            # Check if the directory is an NFS mount point\n            if findmnt --target "$dir" -T nfs 2>/dev/null | grep -q 'nfs'; then\n                echo "$dir is an NFS mount point"\n            else\n                echo "$dir is local"\n            fi\n        else\n            echo "$dir is not a directory"\n        fi\n    done\n}\n\n# Call the function with all arguments passed to the script\nclassify_dirs "$@"