stransfer() {\n    if [ -z "$1" ]; then\n        echo "Usage: stransfer <remote_file>"\n        return 1\n    fi\n\n    current_dir=$(pwd)\n    filename=$(basename "$1")\n    ssh user@remote_host "cat '$current_dir/$1' > /tmp/stransfer_$filename"\n    scp user@remote_host:/tmp/stransfer_$filename ./\n    ssh user@remote_host "rm -f /tmp/stransfer_$filename"\n    echo "Fetched $current_dir/$1 to $PWD/$filename"\n}