#!/bin/sh\n\nJSVC=`which jsvc`\nJAVA_HOME=/usr/lib/jvm/java-8-oracle\nCLASS_PATH=path/to/yourapplication.jar\nUSER=youruser\nHOME=/home/$USER\n\n# Daemon name, the same as the filename of this script\nNAME=MyService\n\ncase "$1" in\n    start)\n\n        echo "Starting $NAME"\n        # Start the service\n        su -c "$JSVC -home $JAVA_HOME -cp $CLASS_PATH \\n                 -user $USER -outfile /var/log/MyService.out -errfile /var/log/MyService.err \\n                 myservice.Main" -s /bin/sh $USER\n\n    ;;\n    stop)\n\n        echo "Stopping $NAME"\n        su -c "$JSVC -home $JAVA_HOME -cp $CLASS_PATH \\n                 -stop -user $USER myservice.Main" -s /bin/sh $USER\n        echo "$NAME stopped"\n\n    ;;\n*)\n        echo "Usage: $0 {start|stop}"\n        exit 1\nesac\n\nexit 0