#!/bin/bash\n\n# Define the path to your perl script\nPERL_SCRIPT="./filter.pl"\n\n# Find all .cpp and .h files in the current directory and its subdirectories\nfor FILE in $(find . -type f \( -iname "*.cpp" -o -iname "*.h" \)); do\n    # Process each file with the Perl script\n    # Assuming your perl script outputs to stdout\n    $PERL_SCRIPT "$FILE" > "${FILE}.filtered"\n    \n    # Overwrite the original file with the filtered content\n    mv "${FILE}.filtered" "$FILE"\ndone