#!/bin/bash\n\n# Create an empty array to hold filenames without suffix\nfiles=()\n\n# Iterate over all .png files in the directory\nfor file in *.png; do\n  # Extract the base filename (without extension)\n  base_filename=$(basename "$file" ".png")\n  \n  # Add to unique set of filenames (before '-1' or '-2')\n  new_name="${base_filename%-*}"\n  if [[ ! " ${files[@]} " =~ " ${new_name} " ]]; then\n    files+=("$new_name")\n  fi\ndone\n\n# Loop through base filenames and handle two images at a time\nfor file in "${files[@]}"; do\n  convert "${file}-1-B.png" "${file}-2-B.png" +append combined_"${file}.png"\ndone\n\n# Combine all combined files into single pdf\nconvert combined_*.png output.pdf