# Install required packages on your old machine (Debian/Ubuntu example)\n   sudo apt-get update\n   sudo apt-get install isc-dhcp-server iptables-persistent\n\n   # Configure DHCP\n   nano /etc/dhcp/dhcpd.conf\n   # Add subnet and range declarations\n\n   # Enable IP forwarding\n   echo "net.ipv4.ip_forward=1" | tee -a /etc/sysctl.conf\n   sudo sysctl -p\n\n   # Set up iptables for NAT and port forwarding (example)\n   iptables -t nat -A POSTROUTING -o eth0 -j MASQUERADE\n   iptables -A FORWARD -i eth1 -o eth0 -m state --state RELATED,ESTABLISHED -j ACCEPT\n   iptables -A FORWARD -i eth0 -o eth1 -j ACCEPT\n\n   # Save iptables rules\n   sudo /etc/init.d/iptables-persistent save\n\n   # Restart DHCP server\n   sudo service isc-dhcp-server restart