# Generate a private key\nopenssl genpkey -algorithm RSA -out private.key\n\n# Create a CSR (Certificate Signing Request) with SANs\nopenssl req -new -key private.key -out csr.pem -config <(cat /etc/ssl/openssl.cnf \\n<(/bin/cat <<EOF\n[req]\nreq_extensions = v3_req\ndistinguished_name = req_distinguished_name\n\n[v3_req]\nsubjectAltName = @alt_names\n\n[alt_names]\nIP.1=192.168.56.1\nIP.2=other-ip-if-any\nDNS.1=jonmorra\nEOF\n))\n\n# Self-sign the certificate\nopenssl x509 -req -days 365 -in csr.pem -signkey private.key -out server.crt\n\n# Convert to JKS format (if necessary for your application)\nkeytool -import -alias mycert -keystore keystore.jks -file server.crt