# Install IPFS\nwget https://dist.ipfs.tech/kubo/v0.19.0/kubo_v0.19.0_linux-amd64.tar.gz\ntar xvfz kubo_v0.19.0_linux-amd64.tar.gz\ncd kubo\nsudo bash install.sh\n\n# Initialize IPFS\nipfs init\n\n# Add a file to the network (it will be stored across peers participating in IPFS)\necho "Hello, World!" > hello_world.txt\nipfs add hello_world.txt\n\n# Output might look like: added QmWPsJr4KbRzU36F1Hs5h9NjYXyBcHhCw4a3e5v18xGzN7 hello_world.txt