#!/bin/bash\n\n# Fetch scheduled tasks from your table and write to the todo directory with timestamps\nmysql -u user -p'password' mydatabase <<EOF > /tmp/job_queue.txt\nSELECT id, command FROM myhappyschedule WHERE is_runnable = 1;\nEOF\n\nwhile read -r line; do\n    echo "$(date +'%Y%m%d%H%M%S')-$line" >> /path/to/todo/jobs\ndone < /tmp/job_queue.txt\n\n# Mark jobs as fetched to avoid doubling them (optional)\nmysql -u user -p'password' mydatabase <<EOF\nUPDATE myhappyschedule SET is_runnable = 0 WHERE is_runnable = 1;\nEOF