#!/bin/bash\n\n# Define source and destination directories\nSOURCE_DIR="/path/to/source"\nDEST_DIR="user@remote:/path/to/destination"\n\n# Define time period (e.g., last 24 hours)\nDURATION=24H\n\n# Use find to locate files modified within the specified duration\nfind "$SOURCE_DIR" -type f -mtime -$DURATION -print0 | \\n    rsync --files-from=- -az --from0 "$SOURCE_DIR"/ "$DEST_DIR"