#!/bin/bash\n\n# Path to ss utility and your project path\nVSS_PATH="C:\Program Files\Microsoft Visual Studio\VSS\win32"\nPROJECT_PATH="$1"\n\n# Change to the VSS tools directory\ncd "$VSS_PATH" || exit\n\n# List checked out files\nss Status -R -I- "$PROJECT_PATH" | grep "Checked out by"\n\nif [ $? -eq 0 ]; then\n    echo "There are checked out files. Please make sure to check them in before proceeding."\nelse\n    echo "No checked out files found. Safe to proceed with removal or other actions."\nfi