#!/bin/bash\n\n# Define the path to your obfuscated jar\nOBF_JAR="path/to/yourobfuscated.jar"\n\n# Create a temporary directory for unzipping\nmkdir temp_obfuscate\ncd temp_obfuscate\n\n# Unzip the jar file\njar -xvf "../$OBF_JAR"\n\n# Example: Replace package names in MANIFEST.MF\n# You need to adjust this part according to your specific obfuscated names and how they are mapped\nsed -i 's/old.package.name/new.obfuscated.name/g' META-INF/MANIFEST.MF\n\n# Rezip the updated contents back into a new jar file\njar -cvf "../updated_$OBF_JAR" .\n\n# Clean up\ncd ..\nrm -rf temp_obfuscate\n\necho "Updated $OBF_JAR to updated_$OBF_JAR with new package names in MANIFEST.MF"