# Ensure Anonymous is Enabled, others are disabled\n  Set-WebConfigurationProperty -pspath 'IIS:\Sites\YourSite\public' -filter "system.webServer/security/authentication/anonymousAuthentication" -name "enabled" -value "True"\n  Set-WebConfigurationProperty -pspath 'IIS:\Sites\YourSite\public' -filter "system.webServer/security/authentication/windowsAuthentication" -name "enabled" -value "False"