# Define paths\n$templatePath = "C:\template.xls"\n$outputPath = "C:\output.xlsx"\n$sheetName = "Sheet1"\n\n# Copy the template file to the output file path\nCopy-Item -Path $templatePath -Destination $outputPath\n\n# Use SQLCmd to run the INSERT statement with OPENROWSET\n$sqlCommand = @"\nDECLARE @sql NVARCHAR(MAX);\nSET @sql = N'INSERT INTO OPENROWSET(''Microsoft.Jet.OLEDB.4.0'', \n    ''Excel 8.0;Database=$outputPath;'', \n    ''SELECT * FROM [$sheetName$]'')\n    SELECT * FROM myTable';\nEXEC sp_executesql @sql;\n"@\n\nsqlcmd -S "(local)" -d "YourDatabaseName" -Q $sqlCommand